import axios from "axios";
import * as XLSX from "xlsx";
export default {
  data() {
    return {
      headers: [{
        text: "Part No",
        value: "part_no"
      }, {
        text: "TG11111",
        value: "tg11111"
      }, {
        text: "TG22222",
